INSERT INTO `cms_article_translation` VALUES ('51', '39', 'vi', 'ABC abc', 'abc-abc', 'croptop from BLCKHRT', '<p class=\"Default\">Trang phục m&agrave;u đen lu&ocirc;n mang lại cho người mặc sự gợi cảm, huyền b&iacute;, v&agrave; quyến rũ. Crop top đen lại c&agrave;ng quyến rũ hơn khi t&ocirc;n l&ecirc;n đường eo thon gọn của người mặc. Chiếc Opera croptop của h&atilde;ng <strong>BLCKHRT</strong> &ndash; viết tắt của blackheart &ndash; đến từ Indonesia, được thiết kế sắc n&eacute;t với những mũi th&ecirc;u tinh tế h&igrave;nh khẩu s&uacute;ng lục theo phong c&aacute;ch full print. Đừng ngại kết hợp với quần jeans lưng cao, những chiếc v&aacute;y ngắn, hoặc quần shorts. V&igrave; l&agrave; &aacute;o sơ mi, bạn cũng c&oacute; thể phối hợp c&ugrave;ng quần t&acirc;y ống xu&ocirc;ng, hoặc mặc n&oacute; như chiếc &aacute;o kho&aacute;c c&ugrave;ng &aacute;o bra dạng ống thật sexy.</p>');
