INSERT INTO `cms_article_translation` VALUES ('56', '40', 'vi', '“Tốt hay Xấu\" - Tất cả trong tay bạn', '“tot-hay-xau-tat-ca-trong-tay-ban', 'Cufflinks', '<p dir=\"ltr\"><span>Khuy măng-s&eacute;t l&agrave; t&iacute;n hiệu của sự quyền lực, được thiết kế d&agrave;nh ri&ecirc;ng cho giới qu&yacute; tộc, những người đ&agrave;n &ocirc;ng thuộc tầng lớp tr&ecirc;n của x&atilde; hội ng&agrave;y xưa v&agrave; chỉ phổ biến v&agrave;o khoảng thế kỷ thứ 17. Khi diện những bộ suit đến c&aacute;c sự kiện lớn, qu&yacute; &ocirc;ng thường dễ bị ho&agrave; v&agrave;o nhau v&igrave; &ldquo;ai cũng giống ai&rdquo;, nhưng ch&iacute;nh nhờ phụ kiện tuy nhỏ m&agrave; lại rất hữu dụng n&agrave;y sẽ gi&uacute;p cho những ai biết tận dụng ch&uacute;ng trở n&ecirc;n nổi bật hơn so với mọi người. Khuy măng-s&eacute;t </span><strong>Blackmarket</strong><span> đặc biệt hơn khi được thiết kế trẻ trung với hai mặt chữ &ldquo;Good&rdquo; - &ldquo;Baad&rdquo; được l&agrave;m từ chất liệu v&agrave; m&agrave;u sắc nổi bật. Chỉ một cử chỉ nhỏ nơi cổ tay, &ldquo;Good or Baad&rdquo; c&aacute;c anh đ&atilde; gửi được th&ocirc;ng điệp đến cho c&aacute;c n&agrave;ng rồi đấy.</span></p>');
INSERT INTO `cms_article_translation` VALUES ('57', '40', 'en', 'Good or Baad', 'good-or-baad', 'Cufflinks', '<p dir=\"ltr\"><span>Resent that black tie gala you are being dragged to next week? If you can&rsquo;t get away with an interesting suit then you might as well indulge your rebellious side with a pair of these &ldquo;good&rdquo; and &ldquo;baad&rdquo; cufflinks. The devil&rsquo;s in the details after all.</span></p>');
