INSERT INTO `cms_article_translation` VALUES ('59', '41', 'vi', 'Quan điểm của Smuthii ', 'quan-diem-cua-smuthii', 'Leather necklace', '<p><span id=\"docs-internal-guid-0470db5d-71a6-8f56-9ec1-5f21a5b3b4b8\"><span>Đ&oacute; l&agrave; một c&aacute;i t&ecirc;n nghe rất lạ được ch&iacute;nh nh&agrave; thiết kế </span><strong>Estelle D&eacute;v&eacute;&rsquo;s</strong><span> đặt cho chiếc v&ograve;ng cổ Smuthii do ch&iacute;nh c&ocirc; thiết kế. Với kiểu d&aacute;ng sang trọng từ chiếc v&ograve;ng cổ m&agrave; Elizabeth Taylor khi thủ vai CLeopatra đ&atilde; sử dụng, c&ugrave;ng sự kết hợp giữa m&agrave;u be đậm v&agrave; c&aacute;c chi tiết bằng v&agrave;ng, th&igrave; chiếc v&ograve;ng cổ bỗng trở th&agrave;nh điểm nhấn ch&iacute;nh thu h&uacute;t nhất của trang phục. Đ&acirc;y ch&iacute;nh l&agrave; một phần nhỏ quan điểm của Smuthii, h&atilde;y kết hợp sao cho trang phục biến th&agrave;nh những phụ trang cho chiếc v&ograve;ng cổ, &iacute;t ra l&agrave; trong lần n&agrave;y, sao phải chần chừ để x&acirc;y dựng h&igrave;nh ảnh mới của bạn bằng chiếc v&ograve;ng cổ Smuthii.</span></span></p>');
INSERT INTO `cms_article_translation` VALUES ('60', '41', 'en', 'Statement Smuthii', 'statement-smuthii', 'Leather necklace', '<p dir=\"ltr\"><strong>Estelle D&eacute;v&eacute;&rsquo;s</strong><span> unusually named Smuthii necklace is a weighty piece of luxury with just a hint of Elizabeth Taylor in a Cleopatra wig about it. The strong beige leather and gold detailing will make it difficult to do anything other than to let your outfit accessorize the necklace, it&rsquo;s a statement piece after all, so just this once, why not build your look around your new Smuthii. </span></p>\r\n<p><span>&nbsp;</span></p>');
